sys_variable_value.value:
(function execute(inputs, outputs) {
    var successCodes = ['200', '202', '201'];
    var arrayUtil = new global.ArrayUtil();
  	var response = JSON.parse(inputs.responseCode);
    if (arrayUtil.contains(successCodes, inputs.status)) {
        outputs.status = 'Success';
      	if(JSON.stringify(response.data[0]) != {})              
      		outputs.comment = response.data[0];
    } else {
      	outputs.status = 'Error';
      	outputs.error_message = response.errorDescription;
    }
})(inputs, outputs);

