sys_variable_value.value:
(function execute(inputs, outputs) {
    var utils = new WrikeUtils();
    utils.validateInputVariables(inputs, outputs);
    utils.validateMandatoryVariables(inputs, ['commentid']);
    utils.mapToOutputFromInput(inputs, outputs, ['commentid']);
})(inputs, outputs);

